mapper.delete:
update tb_user_video uv


    uv.status = #{status}

where uv.userId = #{userId} and uv.videoId = #{videoId}

mapper.select:
    select v.id,v.title,v.url as videoUrl ,v.pic,v.type,v.createTime as pushTime from `tb_video` v where v.title != ""
   AND v.id in (select uv.videoId FROM  tb_user_video uv WHERE uv.userId=#{userId} AND uv.status = 1 ORDER BY uv.createDate DESC
  )
limit #{pageNum},#{count}
